pattern = re.compile(r'const flashcardsData\s*=\s*\[.*?\];', re.DOTALL)

for filepath, new_data in files_and_data:
    with open(filepath, 'r', encoding='utf-8') as f:
        content = f.read()
    
    new_content, count = pattern.subn(new_data, content)
    
    if count == 0:
        print(f'ERROR: No match found in {filepath}')
        continue
    
    # Count flashcards
    fc_count = new_data.count("{ question:")
    
    with open(filepath, 'w', encoding='utf-8') as f:
        f.write(new_content)
    
    print(f'OK: {filepath} — replaced with {fc_count} flashcards')

