#!/usr/bin/env python3
"""Regenerate 100 flashcards for 4 courses in UPEC_LSPS1_S2."""
import re

def replace_flashcards(filepath, flashcards):
    """Replace flashcardsData in an HTML file with new flashcards."""
    with open(filepath, 'r', encoding='utf-8') as f:
        content = f.read()

    lines = []
    for fc in flashcards:
        q = fc['question']
        a = fc['answer']
        lines.append(f"{{ question: '{q}', answer: '{a}', }}")

    new_data = "const flashcardsData = [\n" + "\n".join(lines) + "\n];"

    new_content = re.sub(
        r'const flashcardsData\s*=\s*\[.*?\];',
        new_data,
        content,
        flags=re.DOTALL
    )

    with open(filepath, 'w', encoding='utf-8') as f:
        f.write(new_content)

    count = len(flashcards)
    print(f"  -> {filepath}: {count} flashcards written")
    return count


