#!/usr/bin/env python3
"""Replace flashcardsData blocks in HTML files with new 100-flashcard sets."""
import re
import sys

def replace_flashcards(filepath, new_data_str):
    """Replace const flashcardsData = [...]; in the given HTML file."""
    with open(filepath, 'r', encoding='utf-8') as f:
        content = f.read()
    
    pattern = r'const flashcardsData\s*=\s*\[.*?\];'
    match = re.search(pattern, content, flags=re.DOTALL)
    if not match:
        print(f"ERROR: Could not find flashcardsData in {filepath}")
        sys.exit(1)
    
    old_count = content[match.start():match.end()].count('question:')
    new_content = content[:match.start()] + new_data_str + content[match.end():]
    new_count = new_content.count('question:') - (content.count('question:') - old_count)
    
    with open(filepath, 'w', encoding='utf-8') as f:
        f.write(new_content)
    
    print(f"OK: {filepath} — replaced {old_count} flashcards with {new_count}")

